import {Button, PrinterIcon, Table, TableHead, Theme} from 'client-library';
import React, {useEffect, useMemo, useRef, useState} from 'react';
import {
  immovableInventoryTableHeads,
  immovableTypeOptions,
  movableInventoryTableHeads,
  movableTypeOptions,
  smallInventoryTableHeads,
} from '../../screens/inventoryOverview/constants';
import {InventoryFilters, InventoryFiltersEnum} from '../../screens/inventoryOverview/types';
import useGetSettings from '../../services/graphQL/getSettings/useGetSettings';
import useOrgUnitOfficesGet from '../../services/graphQL/organizationUnitOffices/useOrganizationUnitOfficesGet';
import {DispatchType} from '../../types/graphQL/inventoryDispatch';
import {InventoryItem, SourceType} from '../../types/graphQL/inventoryOverview';
import {InventoryTypeEnum} from '../../types/inventoryType';
import {MicroserviceProps} from '../../types/micro-service-props';
import AssessmentModal from '../assessmentModal/assessmentModal';
import DeactivateModal from '../deactivateModal/deactivateModal';
import MovementModal from '../movementModal/movementModal';
import {FilterDropdown, FilterInput, Filters, ReversButtonContainer} from './styles';
import useInventoryDeactivate from '../../services/graphQL/inventoryDeactivate/useInventoryDeactivate';
import ReceiveInventoryModal from '../receiveInventoryModal/receiveInventoryModal';
import {parseDateForBackend} from '../../utils/dateUtils';
import {filterStatusOptions} from '../movementModal/constants';

interface InventoryListProps {
  context: MicroserviceProps;
  filters: (InventoryFiltersEnum | `${InventoryFiltersEnum}`)[];
  tableData: InventoryItem[];
  loading: boolean;
  onFilter: (value: any, name: string) => void;
  filterValues: InventoryFilters;
  type: InventoryTypeEnum | `${InventoryTypeEnum}`;
  refetch: () => void;
}

const InventoryList = ({
  context,
  filters,
  tableData,
  loading,
  onFilter,
  filterValues,
  type,
  refetch,
}: InventoryListProps) => {
  const [selectedRows, setSelectedRows] = useState<number[]>([]);
  const [movementType, setMovementType] = useState<DispatchType | `${DispatchType}`>();
  const [deactivateModal, setDeactivateModal] = useState(false);
  const [movementModal, setMovementModal] = useState(false);
  const [estimationModal, setEstimationModal] = useState(false);
  const [currentInventoryId, setCurrentInventoryId] = useState<number[]>([]);
  const [currentItem, setCurrentItem] = useState<InventoryItem>();
  const [sourceType, setSourceType] = useState<SourceType | `${SourceType}`>();
  const [returnOrgUnitId, setReturnOrgUnitId] = useState<number>();
  const [receiveModal, setReceiveModal] = useState(false);
  const [currentId, setCurrentId] = useState<number>();
  const prevStateRef = useRef<number[]>(selectedRows);
  const orgUnitId = context?.contextMain?.organization_unit?.id;

  const {options: officeOptions} = useOrgUnitOfficesGet({page: 1, size: 1000, organization_unit_id: Number(orgUnitId)});

  const {options: amortizationGroupOptions} = useGetSettings({
    entity: 'deprecation_types',
  });

  const {options: classTypeOptions} = useGetSettings({entity: 'inventory_class_type'});
  const {mutate: deactivate, loading: loadingDeactivate} = useInventoryDeactivate();

  const {
    navigation: {navigate},
    alert,
  } = context;

  //todo: add useMemo
  const renderFilters = {
    type: (
      <FilterDropdown
        name="source_type"
        value={filterValues.source_type}
        onChange={value => onFilter(value, 'source_type')}
        options={type === 'movable' ? movableTypeOptions : immovableTypeOptions}
        placeholder="Odaberi tip"
        label="TIP:"
      />
    ),
    class: (
      <FilterDropdown
        name="class_type_id"
        value={filterValues.class_type_id}
        onChange={value => onFilter(value, 'class_type_id')}
        options={[{id: 0, title: 'Sve klase'}, ...classTypeOptions]}
        placeholder="Odaberi klasu"
        label="KLASA:"
      />
    ),
    amortization_group: (
      <FilterDropdown
        name="depreciation_group"
        value={filterValues.depreciation_type_id}
        onChange={value => onFilter(value, 'depreciation_type_id')}
        options={[{id: 0, title: 'Sve grupe'}, ...amortizationGroupOptions]}
        placeholder="Odaberi amortizacionu grupu"
        label="AMORTIZACIONA GRUPA:"
      />
    ),
    location: (
      <FilterDropdown
        name="office"
        value={filterValues.office_id}
        onChange={value => onFilter(value, 'office_id')}
        options={[{id: 0, title: 'Sve lokacije'}, ...officeOptions]}
        placeholder="Odaberi lokaciju"
        label="LOKACIJA:"
      />
    ),
    status: (
      <FilterDropdown
        name="status"
        value={filterValues.status}
        onChange={value => onFilter(value, 'status')}
        options={[{id: '', title: 'Sve statusi'}, ...filterStatusOptions]}
        placeholder="Odaberi status"
        label="STATUS:"
      />
    ),
    search: (
      <FilterInput
        name="search"
        value={filterValues.search}
        label="PRETRAGA:"
        placeholder="Pretraga"
        onChange={value => onFilter(value, 'search')}
      />
    ),
  };

  const onCheck = (checked: boolean, currId: number | null) => {
    if (checked && currId) {
      setSelectedRows([...selectedRows, currId]);
    } else {
      setSelectedRows(selectedRows.filter(id => id !== currId));
    }
  };

  const onDeactivate = () => {
    console.log('deactivating');
  };

  const onAddEstimation = (row: InventoryItem) => {
    setCurrentInventoryId([row.id]);
    setEstimationModal(true);
  };

  const onCloseMovementModal = (resetCheckboxes?: boolean) => {
    setMovementModal(false);
    setCurrentItem(undefined);
    setSourceType(undefined);
    setMovementType(undefined);

    if (resetCheckboxes) {
      setSelectedRows([]);
    }
  };

  const onReversClick = () => {
    setMovementType(sourceType?.includes('1') ? 'revers' : 'return-revers');

    setCurrentInventoryId(selectedRows);
    setMovementModal(true);
  };

  const onAddMovement = (item: InventoryItem) => {
    const movementType =
      item.source_type === 'NS1' ? 'revers' : item.source_type === 'NS2' ? 'return-revers' : 'allocation';

    setSourceType(sourceType);
    setMovementType(movementType);
    setCurrentInventoryId([item.id]);
    setCurrentItem(item);
    setMovementModal(true);
  };

  const getTableHeads = useMemo(() => {
    const heads: TableHead[] =
      type === 'movable'
        ? movableInventoryTableHeads
        : type === 'immovable'
        ? immovableInventoryTableHeads
        : smallInventoryTableHeads;

    return [
      ...heads,
      {
        title: '',
        customElement:
          type === 'small' ? null : (
            <ReversButtonContainer>
              <Button
                content={sourceType?.includes('2') ? 'Povrat' : 'Revers'}
                variant="primary"
                size="sm"
                onClick={onReversClick}
                disabled={!selectedRows.length}
              />
            </ReversButtonContainer>
          ),
        accessor: 'TABLE_ACTIONS',
        type: 'tableActions',
      },
    ] as TableHead[];
  }, [type, sourceType, selectedRows]);

  const isReversDone = (row: InventoryItem) => {
    return (
      row.source_type?.includes('1') &&
      row.organization_unit?.id !== row.target_organization_unit?.id &&
      row.target_organization_unit?.id !== 0
    );
  };

  const isCheckboxDisabled = (row: InventoryItem) => {
    if (isReversDone(row) || !row.active) {
      return true;
    }

    if (selectedRows.length && sourceType) {
      if (row.source_type.includes('2') && sourceType?.includes('2')) {
        const currentOrgUnitId = tableData.find(item => item.id === selectedRows[0])?.organization_unit.id;
        setReturnOrgUnitId(currentOrgUnitId);
        return row.organization_unit.id !== currentOrgUnitId;
      } else {
        return row.source_type !== sourceType;
      }
    } else {
      return false;
    }
  };

  useEffect(() => {
    if (prevStateRef.current && !prevStateRef.current.length && selectedRows.length === 1) {
      const currentItemId = selectedRows[0];
      const currentItem = tableData.find(item => item.id === currentItemId);

      setSourceType(currentItem?.source_type);
    }

    if (!selectedRows.length) {
      setSourceType(undefined);
    }
  }, [selectedRows]);

  return (
    <div>
      <Filters>{filters.map(filter => React.cloneElement(renderFilters[filter], {key: filter}))}</Filters>
      <Table
        tableHeads={getTableHeads}
        isLoading={loading}
        data={tableData}
        checkedRows={selectedRows}
        checkboxes={type !== 'small'}
        onCheck={onCheck}
        // eslint-disable-next-line @typescript-eslint/ban-ts-comment
        // @ts-ignore
        tableActions={
          type !== 'small'
            ? [
                {
                  name: 'print',
                  onClick: row => console.log('print'),
                  icon: <PrinterIcon stroke={Theme.palette.gray600} />,
                  disabled: (item: any) => !item.active,
                },
                {
                  name: 'Alokacija',
                  onClick: row => onAddMovement(row),
                  disabled: (item: any) => !item.active,
                },
                {
                  name: 'Dodaj procjenu',
                  onClick: row => onAddEstimation(row),
                  shouldRender: (item: any) => item.source_type?.includes('1'),
                  disabled: (item: any) => !item.active,
                },
                {
                  name: 'Deaktivacija',
                  onClick: row => {
                    setCurrentInventoryId([row.id]);
                    setDeactivateModal(true);
                  },
                  disabled: (item: any) => item.source_type?.includes('2') || item.status !== 'Lager' || !item.active,
                },
              ]
            : [
                {
                  name: 'print',
                  onClick: row => console.log('print'),
                  icon: <PrinterIcon stroke={Theme.palette.gray600} />,
                  disabled: (item: any) => !item.active,
                },
                {
                  name: 'Alokacija',
                  onClick: row => onAddMovement(row),
                  disabled: (item: any) => !item.active,
                },
                {
                  name: 'Deaktivacija',
                  onClick: row => {
                    setCurrentInventoryId([row.id]);
                    setDeactivateModal(true);
                  },
                  disabled: (item: any) => item.source_type?.includes('2') || item.status !== 'Lager' || !item.active,
                },
              ]
        }
        onRowClick={(item: InventoryItem) => {
          if (item.active) {
            navigate(`/inventory/${type}-inventory/${item.id}`);
            context.breadcrumbs.add({
              name: `${item.title}`,
              to: `/inventory/${type}-inventory/${item.id}`,
            });
          }
        }}
        disabledCheckbox={isCheckboxDisabled}
      />
      {/* //TODO ID 1 IS ONLY FOR TESTING, REMOVE LATER */}
      {deactivateModal && (
        <DeactivateModal
          onClose={() => setDeactivateModal(false)}
          loading={loadingDeactivate}
          onDeactivate={({inactive, description}) =>
            deactivate(currentInventoryId[0], parseDateForBackend(inactive) || '', description, () => {
              setCurrentInventoryId([]);
              setDeactivateModal(false);
              alert.success('Osnovno sredstvo je deaktivirano.');
            })
          }
          id={0}
        />
      )}
      {movementModal && (
        <MovementModal
          context={context}
          initialDispatchType={movementType}
          onClose={onCloseMovementModal}
          id={currentInventoryId}
          refetch={refetch}
          sourceType={sourceType}
          returnOrgUnitId={returnOrgUnitId}
          inventoryType={type}
          currentItem={currentItem}
          status={currentItem?.status || ''}
          openReceiveModal={id => {
            setCurrentId(id);
            setReceiveModal(true);
          }}
        />
      )}
      {estimationModal && (
        <AssessmentModal context={context} onClose={() => setEstimationModal(false)} id={currentInventoryId[0]} />
      )}

      {receiveModal && currentId && (
        <ReceiveInventoryModal
          refetch={refetch}
          context={context}
          id={currentId}
          targetOrgID={0}
          onClose={() => {
            setReceiveModal(false);
            setCurrentId(undefined);
          }}
          createRevers={true}
        />
      )}
    </div>
  );
};

export default InventoryList;

import {Datepicker, Dropdown, Modal} from 'client-library';
import {useEffect, useMemo, useState} from 'react';
import {Controller, useForm} from 'react-hook-form';
import useDispatchInsert from '../../services/graphQL/dispatchInsert/useDispatchInsert';
import useOrgUnitOfficesGet from '../../services/graphQL/organizationUnitOffices/useOrganizationUnitOfficesGet';
import useOrganizationUnits from '../../services/graphQL/organizationUnits/useOrganizationUnits';
import useUserProfiles from '../../services/graphQL/userProfileOverview/useUserProfileOverview';
import {DropdownDataNumber, DropdownDataString} from '../../types/dropdownData';
import {DispatchType} from '../../types/graphQL/inventoryDispatch';
import {InventoryItem, SourceType} from '../../types/graphQL/inventoryOverview';
import {InventoryTypeEnum} from '../../types/inventoryType';
import {MicroserviceProps} from '../../types/micro-service-props';
import {parseDate, parseDateForBackend} from '../../utils/dateUtils';
import {immovableTransactionOptions, movableTransactionOptions, smallTransactionOptions} from './constants';
import {MovementForm} from './styles';
import ReceiveInventoryModal from '../receiveInventoryModal/receiveInventoryModal';
import {InventoryDetails} from '../../types/graphQL/inventoryDetails';

interface MovementModalProps {
  context: MicroserviceProps;
  onClose: (resetCheckboxes?: boolean) => void;
  id: number[];
  initialDispatchType?: DispatchType | `${DispatchType}`;
  sourceType?: SourceType | `${SourceType}`;
  refetch: () => void;
  returnOrgUnitId?: number;
  inventoryType: InventoryTypeEnum | `${InventoryTypeEnum}`;
  currentItem?: InventoryItem | InventoryDetails;
  status: string;
  openReceiveModal?: (id: number) => void;
}

const initialFormData: MovementModalForm = {
  transaction: null,
  target_user_profile_id: null,
  target_organization_unit_id: null,
  office_id: null,
  serial_number: '',
  date: new Date(),
};

interface MovementModalForm {
  transaction: DropdownDataString | null;
  target_user_profile_id: DropdownDataNumber | null;
  target_organization_unit_id?: DropdownDataNumber | null;
  office_id: DropdownDataNumber | null;
  serial_number: string;
  date: Date;
}

const MovementModal = ({
  context,
  onClose,
  initialDispatchType,
  sourceType,
  id,
  refetch,
  returnOrgUnitId,
  inventoryType,
  currentItem,
  status,
  openReceiveModal,
}: MovementModalProps) => {
  const {
    handleSubmit,
    formState: {errors, isValid},
    control,
    watch,
    setValue,
  } = useForm<MovementModalForm>({
    defaultValues: initialFormData,
  });

  const {alert} = context;
  const orgUnitId = context.contextMain.organization_unit.id;
  const type = context.navigation.location.pathname.split('/')[2] === 'movable-inventory' ? 'movable' : 'immovable';

  const {options: officeOptions} = useOrgUnitOfficesGet({page: 1, size: 1000, organization_unit_id: Number(orgUnitId)});
  const {options: locationOptions} = useOrganizationUnits(true);
  const {options: userOptions} = useUserProfiles({page: 1, size: 1000, organization_unit_id: orgUnitId});

  const {mutate, loading: isSaving} = useDispatchInsert();

  const onSubmit = (values: MovementModalForm) => {
    if (isValid && !isSaving) {
      const data = {
        source_user_profile_id: 1,
        target_user_profile_id: values.target_user_profile_id?.id ?? 0,
        source_organization_unit_id: orgUnitId,
        target_organization_unit_id: values.target_organization_unit_id?.id ?? 0,
        serial_number: '',
        office_id: values.office_id?.id ?? 0,
        type: (values.transaction?.id as DispatchType) ?? DispatchType.return,
        dispatch_description: '',
        inventory_id: id,
        inventory_type: type,
        date: parseDateForBackend(values?.date),
      };
      try {
        const successTypeString =
          initialDispatchType === 'revers' ? 'revers' : initialDispatchType === 'allocation' ? 'kretanje' : 'povrat';
        const errorTypeString =
          initialDispatchType === 'revers' ? 'revers' : initialDispatchType === 'allocation' ? 'kretanja' : 'povrata';

        mutate(
          data,
          id => {
            if (initialDispatchType !== 'revers') alert.success(`Uspješno ste izvršili ${successTypeString}`);
            onClose(initialDispatchType === 'revers');
            if (initialDispatchType === 'revers' && openReceiveModal) openReceiveModal(id);
            refetch();
          },
          () => {
            alert.error(`Došlo je do greške prilikom ${errorTypeString} sredstva.`);
          },
        );
      } catch (e) {
        console.log(e);
      }
    }
  };

  const isReversDone = (row: InventoryItem | InventoryDetails) => {
    return (
      row.source_type?.includes('1') &&
      row.organization_unit?.id !== row.target_organization_unit?.id &&
      row.target_organization_unit?.id !== 0
    );
  };

  const transactionOptionsToShow = useMemo(() => {
    const options =
      inventoryType === 'movable'
        ? movableTransactionOptions
        : inventoryType === 'immovable'
        ? immovableTransactionOptions
        : smallTransactionOptions;

    const optionsToRemove = [currentItem?.status === 'Lager' ? 'return' : 'allocation'];

    if (
      sourceType?.includes('2') ||
      currentItem?.source_type.includes('2') ||
      (currentItem && isReversDone(currentItem)) ||
      currentItem?.status === 'Zadužen'
    ) {
      optionsToRemove.push('revers');
    }
    if (sourceType?.includes('1') || currentItem?.status === 'Zadužen') {
      optionsToRemove.push('return-revers');
    }

    return options.filter(option => {
      return optionsToRemove.every(optionToRemove => optionToRemove !== option.id);
    });
  }, [sourceType, inventoryType, initialDispatchType]);

  const transactionType = watch('transaction')?.id;

  useEffect(() => {
    if (initialDispatchType) {
      setValue(
        'transaction',
        initialDispatchType ? transactionOptionsToShow.find(option => option.id === initialDispatchType)! : null,
      );
    }
  }, [initialDispatchType, transactionOptionsToShow]);

  useEffect(() => {
    if (locationOptions.length && initialDispatchType && returnOrgUnitId && initialDispatchType === 'return-revers') {
      setValue('target_organization_unit_id', locationOptions.find(option => option.id === returnOrgUnitId)!);
    }
  }, [returnOrgUnitId, initialDispatchType, locationOptions]);

  return (
    <Modal
      open={true}
      onClose={onClose}
      title="KRETANJE SREDSTVA"
      leftButtonOnClick={onClose}
      rightButtonOnClick={handleSubmit(onSubmit)}
      rightButtonText={'Sačuvaj'}
      buttonLoading={isSaving}
      leftButtonText="Otkaži"
      content={
        <MovementForm>
          {currentItem?.status !== 'Zadužen' && (
            <Controller
              name="transaction"
              control={control}
              rules={{required: 'Ovo polje je obavezno'}}
              render={({field: {name, value, onChange}}) => (
                <Dropdown
                  name={name}
                  value={value}
                  onChange={onChange}
                  options={transactionOptionsToShow}
                  label="TIP KRETANJA:"
                  error={errors.transaction?.message}
                />
              )}
            />
          )}

          {transactionType === 'revers' && (
            <Controller
              name="target_organization_unit_id"
              control={control}
              rules={{required: 'Ovo polje je obavezno'}}
              render={({field: {name, value, onChange}}) => (
                <Dropdown
                  name={name}
                  value={value}
                  onChange={onChange}
                  options={locationOptions}
                  label="LOKACIJA:"
                  error={errors.target_organization_unit_id?.message}
                />
              )}
            />
          )}

          {transactionType === 'allocation' && (
            <Controller
              name="target_user_profile_id"
              control={control}
              rules={{required: 'Ovo polje je obavezno'}}
              render={({field: {name, value, onChange}}) => (
                <Dropdown
                  name={name}
                  value={value}
                  onChange={onChange}
                  options={userOptions}
                  label="PRIMALAC:"
                  error={errors.target_user_profile_id?.message}
                />
              )}
            />
          )}
          {transactionType === 'allocation' && (
            <Controller
              name="office_id"
              control={control}
              rules={{required: 'Ovo polje je obavezno'}}
              render={({field: {name, value, onChange}}) => (
                <Dropdown
                  name={name}
                  value={value}
                  onChange={onChange}
                  options={officeOptions}
                  label="LOKACIJA:"
                  error={errors.office_id?.message}
                />
              )}
            />
          )}
          {(transactionType || status == 'Zadužen') && (
            <Controller
              name="date"
              control={control}
              rules={{required: 'Ovo polje je obavezno'}}
              render={({field: {name, value, onChange}}) => (
                <Datepicker
                  onChange={onChange}
                  label={`DATUM ${
                    status == 'Zadužen' ? 'RAZDUŽENJA' : transactionType === 'revers' ? 'REVERSA' : 'ZADUŽENJA'
                  }:`}
                  name={name}
                  value={value ? parseDate(value) : ''}
                  error={errors.date?.message}
                />
              )}
            />
          )}
        </MovementForm>
      }
    />
  );
};

export default MovementModal;

import {Button, Dropdown, Input, TableHead, Theme, TrashIcon, Typography} from 'client-library';
import {useEffect, useState} from 'react';
import {Controller, FormProvider, useFieldArray, useForm} from 'react-hook-form';
import ImmovableAddForm from '../../components/immovableAddForm/immovableAddForm';
import MovableAddForm from '../../components/movableAddForm/movableAddForm';
import SmallInventoryForm from '../../components/smallInventoryForm/smallInventoryForm';
import useGetSettings from '../../services/graphQL/getSettings/useGetSettings';
import useInventoryInsert from '../../services/graphQL/inventoryInsert/useInventoryInsert';
import {InventoryProps} from '../../types/inventoryProps';
import {parseDateForBackend} from '../../utils/dateUtils';
import {newTableItem, tableHeads} from './constants';
import {ButtonContainer, StyledTable} from './styles';
import {DropdownName, InputName, TableItemValues, TableValues, valuesType} from './types';

const InventoryAdd = ({context, type}: InventoryProps) => {
  const [isOrderListSelected, setIsOrderListSelected] = useState(false);
  const isImmovable = type === 'immovable';

  const {
    alert,
    navigation: {navigate},
  } = context;

  const methods = useForm<TableValues>();
  const {
    register,
    handleSubmit,
    control,
    reset,
    formState: {errors, isValid},
    watch,
  } = methods;

  const {fields, append, remove} = useFieldArray({
    control,
    name: 'items',
  });
  const {options: amortizationGroupOptions} = useGetSettings({entity: 'deprecation_types'});

  const {options: classOptions} = useGetSettings({entity: 'inventory_class_type'});
  const {mutate, loading} = useInventoryInsert();

  const updatedTableHeads = tableHeads
    .filter(item => {
      if (type === 'small' && item.accessor === 'depreciation_type') return false;
      return true;
    })
    .map((head: TableHead) => {
      const isDisabled = (head.accessor === 'title' || head.accessor === 'gross_price') && isOrderListSelected;
      const options = head.accessor === 'class_type' ? classOptions : amortizationGroupOptions;

      if (head.type === 'custom') {
        if (head.accessor === 'class_type' || head.accessor === 'depreciation_type') {
          return {
            ...head,
            renderContents: (_: any, __: any, index: any) => {
              return (
                <Controller
                  name={`items.${index}.${head.accessor}` as DropdownName}
                  rules={{required: 'Ovo polje je obavezno'}}
                  control={control}
                  render={({field: {name, value, onChange}}) => (
                    <Dropdown
                      name={name}
                      value={value}
                      onChange={onChange}
                      options={options}
                      // eslint-disable-next-line @typescript-eslint/ban-ts-comment
                      // @ts-ignore
                      error={errors?.items?.[index]?.[`${head.accessor}`]?.message}
                    />
                  )}
                />
              );
            },
          } as TableHead;
        } else {
          return {
            ...head,
            renderContents: (_: any, __: any, index: any) => {
              return (
                <Input
                  type={head.accessor === 'gross_price' ? 'number' : 'text'}
                  {...register(`items.${index}.${head.accessor}` as InputName, {
                    required: head.accessor !== 'description' ? 'Ovo polje je obavezno' : false,
                  })}
                  disabled={isDisabled}
                  // eslint-disable-next-line @typescript-eslint/ban-ts-comment
                  // @ts-ignore
                  error={errors?.items?.[index]?.[`${head.accessor}`]?.message}
                  rightContent={head.accessor === 'gross_price' ? <div>€</div> : undefined}
                />
              );
            },
          } as TableHead;
        }
      } else return head;
    });

  // handlers:

  const onSubmit = async (values: any) => {
    if (isValid) {
      const data = values.items.map((item: any) => ({
        // form data
        id: 0,
        date_of_purchase: parseDateForBackend(values.date_of_purchase) || '',
        source: values.source.id,
        office_id: values.office.id,
        invoice_number: values.invoice_number,
        supplier_id: values.supplier.id,

        // item data
        depreciation_type_id: item?.depreciation_type?.id,
        class_type_id: item.class_type.id,
        inventory_number: item.inventory_number,
        title: item.title,
        description: item.description,
        gross_price: Number(item.gross_price),
        serial_number: item.serial_number,

        // other
        type: type,
        real_estate: null,
        abbreviation: '',
        internal_ownership: true,
        location: '', //?
        target_user_profile_id: 1,
        unit: '',
        amount: 1, // default 1
        net_price: 1,
        donor_title: '',
        price_of_assessment: 0,
        date_of_assessment: null,
        lifetime_of_assessment_in_months: 0,
        active: false,
        deactivation_description: '',
        invoice_file_id: 0,
        file_id: 0,
      }));

      await mutate(
        data,
        () => {
          alert.success('Uspjšeno dodavanje osnovnih sredstava');
          navigate(`/inventory/${type}-inventory`);
        },
        () => alert.error('Neuspjšeno dodavanje osnovnih sredstava'),
      );
    }
  };

  const clearButtonClick = () => {
    reset(); // reset form
    remove(); // clear table
    setIsOrderListSelected(false);
  };

  const handleFormSubmit = (values: valuesType) => {
    if (!isImmovable) {
      if (values && 'order_list' in values && values.order_list && values.order_list.id !== 0) {
        remove(); // clear table
        setIsOrderListSelected(true);
        const articles: TableItemValues[] =
          values?.articles?.map(item => ({
            id: 0,
            class_type: {id: 1, title: 'Class 1'},
            depreciation_type: {id: 1, title: 'Amortizaciona grupa'},
            inventory_number: '',
            title: item.title,
            serial_number: '',
            gross_price: item?.total_price?.toString(),
            description: '',
          })) || [];
        append(articles); // should append data from selected order_list
      } else if (!values?.invoice_number) {
        remove();
        setIsOrderListSelected(false);
      } else append(newTableItem);
    }
  };

  const calculateTotal = () => {
    const items = watch('items');
    let sum = 0;
    if (items) {
      items.forEach((item: TableItemValues) => {
        const price = parseFloat(item?.gross_price?.toString() || '0');
        sum += price;
      });
    }
    return sum;
  };

  const renderFormByType = {
    movable: <MovableAddForm context={context} onFormSubmit={handleFormSubmit} />,
    immovable: <ImmovableAddForm context={context} />,
    small: <SmallInventoryForm context={context} onFormSubmit={handleFormSubmit} />,
  };

  useEffect(() => {
    calculateTotal();
  }, [watch('items')]);

  return (
    <FormProvider {...methods}>
      {renderFormByType[type]}

      {!isImmovable && (
        <>
          <StyledTable
            data={fields as TableItemValues[]}
            tableHeads={updatedTableHeads}
            tableActions={
              isOrderListSelected
                ? []
                : [
                    {
                      name: 'delete',
                      onClick: (item: TableItemValues) => remove(fields.findIndex(field => field.id === item.id)),
                      icon: <TrashIcon stroke={Theme?.palette?.gray800} />,
                    },
                  ]
            }
            style={{marginBottom: '20px'}}
          />

          <Input
            label="TOTAL:"
            style={{width: '300px'}}
            value={calculateTotal().toString()}
            leftContent={<Typography content="€" />}
            disabled
          />

          <ButtonContainer>
            <Button content="Odustani" onClick={clearButtonClick} />
            <Button content="Sačuvaj" onClick={handleSubmit(onSubmit)} variant="primary" loader={loading} />
          </ButtonContainer>
        </>
      )}
    </FormProvider>
  );
};
export default InventoryAdd;

import {Pagination} from 'client-library';
import {useState} from 'react';
import InventoryDispatchList from '../../components/inventoryDispatchList/inventoryDispatchList';
import {PAGE_SIZE} from '../../constants';
import useInventoryDispatchOverview from '../../services/graphQL/inventoryDispatchOverview/useInventoryDispatchOverview';
import {InventoryProps} from '../../types/inventoryProps';
import {initialReceiveFilterValues} from './constants';
import {InventoryDispatchFilters} from './types';

const InventoryDispatch = ({context}: InventoryProps) => {
  const [page, setPage] = useState<number>(0);
  const [filterValues, setFilterValues] = useState<InventoryDispatchFilters>(initialReceiveFilterValues);

  const onFilter = (value: any, name: string) => {
    setFilterValues({...filterValues, [name]: value});
  };
  const type = context.navigation.location.pathname.split('/')[2] === 'movable-inventory' ? 'movable' : 'immovable';

  const {data, refetch, loading} = useInventoryDispatchOverview({
    page,
    size: PAGE_SIZE,
    ...filterValues,
    inventory_type: type,
    id: 0,
    type: 'revers',
  });

  const onPageChange = (page: number) => {
    setPage(page);
  };

  return (
    <div>
      <InventoryDispatchList
        refetch={refetch}
        context={context}
        tableData={data.items}
        loading={loading}
        onFilter={onFilter}
        filterValues={filterValues}
      />
      <Pagination
        pageCount={data.total! / PAGE_SIZE}
        onChange={onPageChange}
        variant="filled"
        itemsPerPage={PAGE_SIZE}
        previousLabel="Prethodna"
        nextLabel="Sledeća"
        pageRangeDisplayed={3}
        style={{marginTop: '20px'}}
      />
    </div>
  );
};

export default InventoryDispatch;
