import {Button, DownloadIcon, Table, TableHead, Theme, Typography} from 'client-library';
import React, {useEffect, useMemo, useRef, useState} from 'react';
import {StatusesForMovableInventory} from '../../constants.ts';
import useAppContext from '../../context/useAppContext';
import {
  immovableTypeOptions,
  movableInventoryTableHeads,
  movableTypeOptions,
  smallInventoryTableHeads,
} from '../../screens/inventoryOverview/constants';
import {InventoryFilters, InventoryFiltersEnum} from '../../screens/inventoryOverview/types';
import {REQUEST_STATUSES} from '../../services/constants.ts';
import useAssessmentArrayInsert from '../../services/graphQL/assessmentInsertArray/useAssessmentInsertAdrray.ts';
import useGetSettings from '../../services/graphQL/getSettings/useGetSettings';
import useInventoryDeactivate from '../../services/graphQL/inventoryDeactivate/useInventoryDeactivate';
import useInventoryDetails from '../../services/graphQL/inventoryDetails/useInventoryDetailsGet';
import useInventoriesExpireOverview from '../../services/graphQL/inventoryOverview/useInventoriesExpireOverview';
import useOrgUnitOfficesGet from '../../services/graphQL/organizationUnitOffices/useOrganizationUnitOfficesGet';
import useOrganizationUnits from '../../services/graphQL/organizationUnits/useOrganizationUnits';
import {uploadExpireInventoryXls} from '../../services/uploadExpireInventoryXls.tsx';
import {InventoryExpireItem} from '../../types/files.ts';
import {InventoryAssessmentData} from '../../types/graphQL/inventoryAssessment.ts';
import {DispatchType} from '../../types/graphQL/inventoryDispatch';
import {InventoryItem, SourceType} from '../../types/graphQL/inventoryOverview';
import {InventoryTypeEnum} from '../../types/inventoryType';
import {MicroserviceProps} from '../../types/micro-service-props';
import {parseDateForBackend} from '../../utils/dateUtils';
import AssessmentModal from '../assessmentModal/assessmentModal';
import DeactivateModal from '../deactivateModal/deactivateModal';
import {filterExpireOptions, filterStatusOptions} from '../movementModal/constants';
import MovementModal from '../movementModal/movementModal';
import ReceiveInventoryModal from '../receiveInventoryModal/receiveInventoryModal';
import {FilterDropdown, FilterInput, Filters, ReversButtonContainer} from './styles';
import {DropdownDataNumber} from '../../types/dropdownData.ts';

interface InventoryListProps {
  context: MicroserviceProps;
  filters: (InventoryFiltersEnum | `${InventoryFiltersEnum}`)[];
  tableData: InventoryItem[];
  loading: boolean;
  onFilter: (value: any, name: string) => void;
  filterValues: InventoryFilters;
  type: InventoryTypeEnum | `${InventoryTypeEnum}`;
  refetch: () => void;
}

const InventoryList = ({
  context,
  filters,
  tableData,
  loading,
  onFilter,
  filterValues,
  type,
  refetch,
}: InventoryListProps) => {
  const [selectedRows, setSelectedRows] = useState<number[]>([]);
  const [movementType, setMovementType] = useState<DispatchType>();
  const [deactivateModal, setDeactivateModal] = useState(false);
  const [movementModal, setMovementModal] = useState(false);
  const [estimationModal, setEstimationModal] = useState(false);
  const [currentInventoryId, setCurrentInventoryId] = useState<number[]>([]);
  const [currentItem, setCurrentItem] = useState<InventoryItem>();
  const [sourceType, setSourceType] = useState<SourceType>();
  const [returnOrgUnitId, setReturnOrgUnitId] = useState<number>();
  const [receiveModal, setReceiveModal] = useState(false);
  const [currentId, setCurrentId] = useState<number>();
  const prevStateRef = useRef<number[]>(selectedRows);
  const orgUnitId = context?.contextMain?.organization_unit?.id;

  const {options: officeOptions} = useOrgUnitOfficesGet({page: 1, size: 1000, organization_unit_id: Number(orgUnitId)});
  const {options: organizationUnits} = useOrganizationUnits(true);

  const {mutate: mutateAssessmentArray, loading: isSavingAssessmentArray} = useAssessmentArrayInsert();

  const {
    reportService: {generatePdf},
    spreadsheetService: {openImportModal},
    contextMain: {token, id: user_profile_id},
  } = useAppContext();
  const {refetch: fetchDetails} = useInventoryDetails();

  const {options: amortizationGroupOptions} = useGetSettings({
    entity: 'deprecation_types',
  });

  const {options: classTypeOptions} = useGetSettings({entity: 'inventory_class_type'});
  const {mutate: deactivate, loading: loadingDeactivate} = useInventoryDeactivate();
  const {options: realEstateTypeOptions} = useGetSettings({entity: 'real_estate_types'});

  const immovableInventoryTableHeads: TableHead[] = [
    {title: 'Tip', accessor: 'source_type'},
    {
      title: 'Vrsta nepokretnosti',
      accessor: 'real_estate',
      type: 'custom',
      renderContents: (real_estate: any, item: InventoryItem) => <Typography content={real_estate.type_id} />,
    },
    {
      title: 'Površina m2',
      accessor: '',
      type: 'custom',
      renderContents: (_: any, item: InventoryItem) => <Typography content={item.real_estate?.square_area} />,
    },
    {
      title: 'Amortizaciona grupa',
      accessor: 'depreciation_type',
      type: 'custom',
      renderContents: (value: DropdownDataNumber) => <Typography content={value.title} />,
    },
    {
      title: 'Lokacija',
      accessor: 'location',
    },
    {
      title: 'Status',
      accessor: 'status',
    },
  ];

  const {
    navigation: {navigate},
    alert,
  } = context;

  //todo: add useMemo
  const renderFilters = {
    type: (
      <FilterDropdown
        name="source_type"
        value={filterValues.source_type}
        onChange={value => onFilter(value, 'source_type')}
        options={type === 'movable' ? movableTypeOptions : immovableTypeOptions}
        placeholder="Odaberi tip"
        label="TIP:"
      />
    ),
    class: (
      <FilterDropdown
        name="class_type_id"
        value={filterValues.class_type_id}
        onChange={value => onFilter(value, 'class_type_id')}
        options={[{id: 0, title: 'Sve klase'}, ...classTypeOptions]}
        placeholder="Odaberi klasu"
        label="KLASA:"
      />
    ),
    amortization_group: (
      <FilterDropdown
        name="depreciation_group"
        value={filterValues.depreciation_type_id}
        onChange={value => onFilter(value, 'depreciation_type_id')}
        options={[{id: 0, title: 'Sve grupe'}, ...amortizationGroupOptions]}
        placeholder="Odaberi amortizacionu grupu"
        label="AMORTIZACIONA GRUPA:"
      />
    ),
    location: (
      <FilterDropdown
        name="office"
        value={filterValues.office_id}
        onChange={value => onFilter(value, 'office_id')}
        options={[{id: 0, title: 'Sve lokacije'}, ...officeOptions]}
        placeholder="Odaberi lokaciju"
        label="LOKACIJA:"
      />
    ),
    organization_unit:
      orgUnitId === 3 ? (
        <FilterDropdown
          name="organization_unit_id"
          value={filterValues.organization_unit_id}
          onChange={value => onFilter(value, 'organization_unit_id')}
          options={[{id: 0, title: 'Sve organizacione jedinice'}, ...organizationUnits]}
          placeholder="Odaberi organizacionu jedinicu"
          label="ORGANIZACIONA JEDINICA:"
        />
      ) : (
        <></>
      ),
    status: (
      <FilterDropdown
        name="status"
        value={filterValues.status}
        onChange={value => onFilter(value, 'status')}
        options={[{id: '', title: 'Svi statusi'}, ...filterStatusOptions]}
        placeholder="Odaberi status"
        label="STATUS:"
      />
    ),
    type_of_immovable_property: (
      <FilterDropdown
        name="type_of_immovable_property"
        value={filterValues.type_of_immovable_property}
        onChange={value => onFilter(value, 'type_of_immovable_property')}
        options={[{id: '', title: 'Sve vrste nepokretnosti'}, ...realEstateTypeOptions]}
        placeholder="Odaberi vrstu nepokretnosti"
        label="VRSTA NEPOKRETNOSTI:"
      />
    ),
    search: (
      <FilterInput
        name="search"
        value={filterValues.search}
        label="PRETRAGA:"
        placeholder="Pretraga"
        onChange={value => onFilter(value, 'search')}
      />
    ),
  };

  const onCheck = (checked: boolean, currId: number | null) => {
    if (checked && currId) {
      setSelectedRows([...selectedRows, currId]);
    } else {
      setSelectedRows(selectedRows.filter(id => id !== currId));
    }
  };

  const {fetch: fetchInventoriesExpire} = useInventoriesExpireOverview();

  const fetchPDFUrl = (id: number) => {
    fetchDetails(id, data => {
      setSourceType(data.source_type);
      if (data.source_type.includes('NS')) {
        generatePdf('IMMOVABLE_INVENTORY_REPORT', data);
      } else {
        generatePdf('MOVABLE_INVENTORY_REPORT', data);
      }
    });
  };

  const onAddEstimation = (row: InventoryItem) => {
    setCurrentInventoryId([row.id]);
    setEstimationModal(true);
  };

  const onCloseMovementModal = (resetCheckboxes?: boolean) => {
    setMovementModal(false);
    setCurrentItem(undefined);
    setSourceType(undefined);
    setMovementType(undefined);

    if (resetCheckboxes) {
      setSelectedRows([]);
    }
  };

  const onReversClick = () => {
    setMovementType(
      filterValues.source_type?.title.includes('Donacije')
        ? DispatchType.convert
        : sourceType?.includes('1')
        ? DispatchType.revers
        : DispatchType.returnRevers,
    );

    setCurrentInventoryId(selectedRows);
    setMovementModal(true);
  };

  const onAddMovement = (item: InventoryItem) => {
    const movementType =
      item.source_type === 'NS1'
        ? DispatchType.revers
        : item.source_type === 'NS2'
        ? DispatchType.returnRevers
        : DispatchType.allocation;

    setSourceType(sourceType);
    setMovementType(movementType);
    setCurrentInventoryId([item.id]);
    setCurrentItem(item);
    setMovementModal(true);
  };

  const onSubmitUploadedTable = async (
    assessments: InventoryExpireItem[],
    options: {onSuccess: () => void; onError: () => void},
  ) => {
    const payload: InventoryAssessmentData[] = assessments?.map(item => ({
      inventory_id: item.inventory_id,
      active: item.active,
      depreciation_type_id: item.depreciation_type_id,
      user_profile_id: user_profile_id,
      estimated_duration: item.estimated_duration,
      gross_price_difference: item.gross_price_difference,
      date_of_assessment: item.date_of_assessment,
      type: item.type,
      residual_price: item.residual_price,
    }));
    await mutateAssessmentArray(
      payload,
      () => {
        alert.success('Podaci za artikle kojima ističe amortizacija su uspješno ažurirani.');
        refetch();
        options.onSuccess();
      },
      () => {
        alert.error('Podaci za artikle kojima ističe amortizacija nisu uspješno ažurirani.');
        options.onError();
      },
    );
  };

  const handleUploadTable = async (files: FileList) => {
    const response = await uploadExpireInventoryXls(files[0], token);

    if (response?.status === REQUEST_STATUSES.success) {
      if (response?.data?.length) {
        return response.data;
      }
      return null;
    } else {
      alert.error('Došlo je do greške prilikom učitavanja fajla!');
    }
  };

  const onDispatchClick = () => {
    if (type === 'small') return;

    fetchInventoriesExpire(type, items => {
      if (items.length > 0) {
        const props = {
          type: 'EXPIRE_INVENTORIES',
          data: {inventories: items, type},
          onSubmit: onSubmitUploadedTable,
          handleUpload: handleUploadTable,
          content: 'Tabela amortizacije',
        };
        openImportModal(props);
      } else {
        alert.info('Nema artikala kojima ističe amortizacije na 31.12. tekuće godine.');
      }
    });
  };

  const getTableHeads = useMemo(() => {
    const heads: TableHead[] =
      type === 'movable'
        ? movableInventoryTableHeads
        : type === 'immovable'
        ? immovableInventoryTableHeads
        : smallInventoryTableHeads;

    return [
      ...heads,
      {
        title: '',
        accessor: 'TABLE_ACTIONS',
        type: 'tableActions',
      },
    ] as TableHead[];
  }, [type, sourceType, selectedRows]);

  const isReversDone = (row: InventoryItem) => {
    return (
      row.source_type?.includes('1') &&
      row.organization_unit?.id !== row.target_organization_unit?.id &&
      row.target_organization_unit?.id !== 0
    );
  };

  const isCheckboxDisabled = (row: InventoryItem) => {
    if (
      isReversDone(row) ||
      !row.active ||
      (row?.target_organization_unit?.id && row?.target_organization_unit?.id !== orgUnitId) ||
      row.status == StatusesForMovableInventory.POVRACAJ ||
      row.status == StatusesForMovableInventory.ZADUZENO
    ) {
      return true;
    }

    if (row.status === 'Revers' && row?.target_organization_unit?.id !== orgUnitId) return true;

    if (selectedRows.length && sourceType) {
      if (row.source_type.includes('2') && sourceType?.includes('2')) {
        const currentOrgUnitId = tableData.find(item => item.id === selectedRows[0])?.organization_unit.id;
        setReturnOrgUnitId(currentOrgUnitId);
        return row.organization_unit.id !== currentOrgUnitId;
      } else {
        return row.source_type !== sourceType;
      }
    } else {
      return false;
    }
  };

  useEffect(() => {
    if (prevStateRef.current && !prevStateRef.current.length && selectedRows.length === 1) {
      const currentItemId = selectedRows[0];
      const currentItem = tableData.find(item => item.id === currentItemId);

      setSourceType(currentItem?.source_type);
    }

    if (!selectedRows.length) {
      setSourceType(undefined);
    }
  }, [selectedRows]);

  return (
    <div>
      <Filters>{filters.map(filter => React.cloneElement(renderFilters[filter], {key: filter}))}</Filters>
      <ReversButtonContainer>
        {type !== InventoryTypeEnum.SMALL && (
          <Button content={'Amortizacija'} variant="primary" size="sm" onClick={onDispatchClick} />
        )}

        {type === InventoryTypeEnum.MOVABLE && (
          <Button
            content={
              filterValues.source_type?.title.includes('Donacije')
                ? 'Konvertovanje PS2 sredstva u PS1'
                : sourceType?.includes('2')
                ? 'Povrat'
                : 'Eksterni revers'
            }
            variant="primary"
            size="sm"
            onClick={onReversClick}
            disabled={!selectedRows.length}
            style={{marginLeft: '10px'}}
          />
        )}
      </ReversButtonContainer>
      <Table
        tableHeads={getTableHeads}
        isLoading={loading}
        data={tableData}
        checkedRows={selectedRows}
        checkboxes={type === InventoryTypeEnum.MOVABLE}
        onCheck={onCheck}
        // eslint-disable-next-line @typescript-eslint/ban-ts-comment
        // @ts-ignore
        tableActions={
          type !== 'small'
            ? [
                {
                  name: 'print',
                  onClick: row => fetchPDFUrl(row.id),
                  icon: <DownloadIcon stroke={Theme.palette.gray600} />,
                },
                {
                  name: 'Alokacija',
                  onClick: row => onAddMovement(row),
                  disabled: (item: InventoryItem) =>
                    (item.target_organization_unit.id && orgUnitId !== item.target_organization_unit.id) ||
                    !item.active ||
                    item.status === StatusesForMovableInventory.POSLATO ||
                    item.status === StatusesForMovableInventory.PRIHVACENO ||
                    item.status === StatusesForMovableInventory.ARHIVA ||
                    item.status === StatusesForMovableInventory.POVRACAJ,
                  shouldRender: (item: InventoryItem) => item.type !== InventoryTypeEnum.IMMOVABLE,
                },
                {
                  name: 'Dodaj procjenu',
                  onClick: row => onAddEstimation(row),
                  shouldRender: (item: any) => item.source_type?.includes('1'),
                  disabled: (item: InventoryItem) =>
                    (item.target_organization_unit.id && orgUnitId !== item.target_organization_unit.id) ||
                    !item.active,
                },
                {
                  name: 'Otpis',
                  onClick: row => {
                    setCurrentInventoryId([row.id]);
                    setDeactivateModal(true);
                  },
                  disabled: (item: InventoryItem) =>
                    type === InventoryTypeEnum.IMMOVABLE ||
                    item.source_type?.includes('2') ||
                    item.status !== StatusesForMovableInventory.NEZADUZENO ||
                    (item.target_organization_unit.id && orgUnitId !== item.target_organization_unit.id) ||
                    !item.active,
                },
              ]
            : [
                {
                  name: 'print',
                  onClick: row => fetchPDFUrl(row.id),
                  icon: <DownloadIcon stroke={Theme.palette.gray600} />,
                },
                {
                  name: 'Alokacija',
                  onClick: row => onAddMovement(row),
                  disabled: (item: InventoryItem) => orgUnitId !== item.target_organization_unit.id || !item.active,
                },
                {
                  name: 'Otpis',
                  onClick: row => {
                    setCurrentInventoryId([row.id]);
                    setDeactivateModal(true);
                  },
                  disabled: (item: InventoryItem) =>
                    item.source_type?.includes('2') ||
                    item.status !== 'Nezaduženo' ||
                    orgUnitId !== item.target_organization_unit.id ||
                    !item.active,
                },
              ]
        }
        onRowClick={(item: InventoryItem) => {
          navigate(`/inventory/${type}-inventory/${item.id}`);
          context.breadcrumbs.add({
            name: `${item.title}`,
            to: `/inventory/${type}-inventory/${item.id}`,
          });
        }}
        disabledCheckbox={isCheckboxDisabled}
      />
      {/* //TODO ID 1 IS ONLY FOR TESTING, REMOVE LATER */}
      {deactivateModal && (
        <DeactivateModal
          onClose={() => setDeactivateModal(false)}
          loading={loadingDeactivate}
          onDeactivate={({inactive, description, file_id}) =>
            deactivate(
              currentInventoryId[0],
              parseDateForBackend(new Date(inactive)) || '',
              description,
              file_id,
              () => {
                setCurrentInventoryId([]);
                setDeactivateModal(false);
                alert.success('Osnovno sredstvo je otpisano.');
              },
            )
          }
          id={0}
        />
      )}
      {movementModal && (
        <MovementModal
          context={context}
          initialDispatchType={movementType}
          onClose={onCloseMovementModal}
          id={currentInventoryId}
          refetch={refetch}
          sourceType={sourceType}
          returnOrgUnitId={returnOrgUnitId}
          inventoryType={type}
          currentItem={currentItem}
          status={currentItem?.status || ''}
          openReceiveModal={id => {
            setCurrentId(id);
            setReceiveModal(true);
          }}
        />
      )}
      {estimationModal && (
        <AssessmentModal
          context={context}
          onClose={() => setEstimationModal(false)}
          id={currentInventoryId[0]}
          depreciation_type_id={currentItem?.depreciation_type?.id || 0}
        />
      )}

      {receiveModal && currentId && (
        <ReceiveInventoryModal
          refetch={refetch}
          context={context}
          id={currentId}
          targetOrgID={0}
          onClose={() => {
            setReceiveModal(false);
            setCurrentId(undefined);
          }}
          createRevers={true}
          shouldGeneratePDF={true}
        />
      )}
    </div>
  );
};

export default InventoryList;

import {Datepicker, Dropdown, Modal, FileUpload, Typography} from 'client-library';
import {useEffect, useMemo, useState} from 'react';
import {Controller, useForm} from 'react-hook-form';
import useDispatchInsert from '../../services/graphQL/dispatchInsert/useDispatchInsert';
import useOrgUnitOfficesGet from '../../services/graphQL/organizationUnitOffices/useOrganizationUnitOfficesGet';
import useOrganizationUnits from '../../services/graphQL/organizationUnits/useOrganizationUnits';
import useUserProfiles from '../../services/graphQL/userProfileOverview/useUserProfileOverview';
import {DropdownDataNumber, DropdownDataString} from '../../types/dropdownData';
import {InventoryDetails} from '../../types/graphQL/inventoryDetails';
import {DispatchType} from '../../types/graphQL/inventoryDispatch';
import {InventoryItem, SourceType} from '../../types/graphQL/inventoryOverview';
import {InventoryTypeEnum} from '../../types/inventoryType';
import {MicroserviceProps} from '../../types/micro-service-props';
import {parseDateForBackend} from '../../utils/dateUtils';
import {immovableTransactionOptions, movableTransactionOptions, smallTransactionOptions} from './constants';
import {FileUploadWrapper, MovementForm} from './styles';
import {StatusesForMovableInventory} from '../../constants';

interface MovementModalProps {
  context: MicroserviceProps;
  onClose: (resetCheckboxes?: boolean) => void;
  id: number[];
  initialDispatchType?: DispatchType | `${DispatchType}`;
  sourceType?: SourceType | `${SourceType}`;
  refetch: () => void;
  returnOrgUnitId?: number;
  inventoryType: InventoryTypeEnum | `${InventoryTypeEnum}`;
  currentItem?: InventoryItem | InventoryDetails;
  status: string;
  openReceiveModal?: (id: number) => void;
  minDate?: string;
}

const initialFormData: MovementModalForm = {
  transaction: null,
  target_user_profile_id: null,
  target_organization_unit_id: null,
  office_id: null,
  serial_number: '',
  date: new Date(),
  file_id: 0,
};

interface MovementModalForm {
  transaction: DropdownDataString | null;
  target_user_profile_id: DropdownDataNumber | null;
  target_organization_unit_id?: DropdownDataNumber | null;
  office_id: DropdownDataNumber | null;
  serial_number: string;
  date: Date;
  file_id?: number;
}

const MovementModal = ({
  context,
  onClose,
  initialDispatchType,
  sourceType,
  id,
  refetch,
  returnOrgUnitId,
  inventoryType,
  currentItem,
  status,
  openReceiveModal,
  minDate,
}: MovementModalProps) => {
  const {
    handleSubmit,
    formState: {errors, isValid},
    control,
    watch,
    setValue,
    setError,
    clearErrors,
  } = useForm<MovementModalForm>({
    defaultValues: initialFormData,
  });

  const {
    alert,
    fileService: {uploadFile},
  } = context;
  const orgUnitId = context.contextMain.organization_unit.id;
  const type = context.navigation.location.pathname.split('/')[2] === 'movable-inventory' ? 'movable' : 'immovable';
  const {options: officeOptions} = useOrgUnitOfficesGet({page: 1, size: 1000, organization_unit_id: Number(orgUnitId)});
  const {options: locationOptions} = useOrganizationUnits(true);
  const {options: userOptions} = useUserProfiles({page: 1, size: 1000, organization_unit_id: orgUnitId});
  const [files, setFiles] = useState<FileList | null>(null);

  const {mutate, loading: isSaving} = useDispatchInsert();

  const onSubmit = async (values: MovementModalForm) => {
    if (isValid && !isSaving) {
      if (files && files.length) {
        const formData = new FormData();
        const fileArray = Array.from(files);

        formData.append('file', fileArray[0]);

        await uploadFile(
          formData,
          (res: any) => {
            setFiles(null);
            setValue('file_id', res[0]?.id);
            handleDispatchInsert({...values, file_id: res[0]?.id});
          },
          () => {
            context?.alert.error('Greška pri čuvanju! Fajlovi nisu učitani.');
          },
        );

        return;
      } else {
        handleDispatchInsert(values);
      }
    }
  };

  const handleDispatchInsert = async (values: MovementModalForm) => {
    const file_id = watch('file_id');

    const data = {
      source_user_profile_id: 1,
      target_user_profile_id: values.target_user_profile_id?.id ?? 0,
      source_organization_unit_id: orgUnitId,
      target_organization_unit_id: values.target_organization_unit_id?.id ?? 0,
      serial_number: '',
      office_id: values.office_id?.id ?? 0,
      type: (values.transaction?.id as DispatchType) ?? DispatchType.return,
      dispatch_description: '',
      inventory_id: id,
      inventory_type: type,
      date: parseDateForBackend(values?.date),
      file_id: file_id,
    };

    if (transactionType === DispatchType.returnRevers && currentItem?.organization_unit?.id) {
      data.target_organization_unit_id = currentItem?.organization_unit?.id;
    }
    try {
      const successTypeString =
        transactionType === DispatchType.revers
          ? 'kreirali revers'
          : transactionType === DispatchType.allocation
          ? 'izvršili kretanje'
          : transactionType === DispatchType.convert
          ? 'izvršili konvertovanje PS2 sredstva u PS1'
          : 'izvršili povrat';
      const errorTypeString =
        transactionType === DispatchType.revers
          ? DispatchType.revers
          : transactionType === DispatchType.allocation
          ? 'kretanja'
          : transactionType === DispatchType.convert
          ? 'konvertovanje'
          : 'povrata';
      mutate(
        data,
        id => {
          if (initialDispatchType !== DispatchType.revers) alert.success(`Uspješno ste  ${successTypeString}`);
          onClose(initialDispatchType === DispatchType.revers);
          if (initialDispatchType === DispatchType.revers && openReceiveModal) openReceiveModal(id);
          refetch();
        },
        () => {
          alert.error(`Došlo je do greške prilikom ${errorTypeString} sredstva.`);
        },
      );
    } catch (e) {
      console.log(e);
    }
  };

  const isReversDone = (row: InventoryItem | InventoryDetails) => {
    return (
      row.source_type?.includes('1') &&
      row.organization_unit?.id !== row.target_organization_unit?.id &&
      row.target_organization_unit?.id !== 0
    );
  };

  const transactionOptionsToShow = useMemo(() => {
    const options =
      inventoryType === 'movable'
        ? movableTransactionOptions
        : inventoryType === 'immovable'
        ? immovableTransactionOptions
        : smallTransactionOptions;

    const optionsToRemove = [
      currentItem?.status === StatusesForMovableInventory.NEZADUZENO ? DispatchType.return : DispatchType.allocation,
    ];

    if (!currentItem?.is_external_donation && initialDispatchType !== DispatchType.convert)
      optionsToRemove.push(DispatchType.convert);

    if (
      sourceType?.includes('2') ||
      currentItem?.source_type.includes('2') ||
      (currentItem && isReversDone(currentItem)) ||
      currentItem?.status === StatusesForMovableInventory.ZADUZENO ||
      currentItem?.is_external_donation ||
      initialDispatchType === DispatchType.convert
    ) {
      optionsToRemove.push(DispatchType.revers);
    }

    if (
      sourceType?.includes('1') ||
      currentItem?.status === StatusesForMovableInventory.ZADUZENO ||
      currentItem?.is_external_donation ||
      initialDispatchType === DispatchType.convert
    ) {
      optionsToRemove.push(DispatchType.returnRevers);
    }

    if (currentItem?.source_type?.includes('1') && currentItem?.status === StatusesForMovableInventory.NEZADUZENO) {
      optionsToRemove.push(DispatchType.returnRevers);
    }

    return options.filter(option => {
      return optionsToRemove.every(optionToRemove => optionToRemove !== option.id);
    });
  }, [sourceType, inventoryType, initialDispatchType]);

  const transactionType = watch('transaction')?.id;

  useEffect(() => {
    if (initialDispatchType) {
      setValue(
        'transaction',
        initialDispatchType ? transactionOptionsToShow.find(option => option.id === initialDispatchType)! : null,
      );
    }
  }, [initialDispatchType, transactionOptionsToShow]);

  useEffect(() => {
    if (minDate) {
      setValue('date', new Date(minDate));
    }
  }, [minDate]);

  useEffect(() => {
    if (
      locationOptions.length &&
      initialDispatchType &&
      returnOrgUnitId &&
      initialDispatchType === DispatchType.returnRevers
    ) {
      setValue('target_organization_unit_id', locationOptions.find(option => option.id === returnOrgUnitId)!);
    }
  }, [returnOrgUnitId, initialDispatchType, locationOptions]);

  const handleUpload = (files: FileList) => {
    const allowedSize = 1048576;
    if (files && files[0] && files[0].size > allowedSize) {
      setError('file_id', {type: 'custom', message: 'Maksimalna veličina fajla je 1MB.'});
      return;
    } else {
      setFiles(files);
      clearErrors('file_id');
      context.alert.success('Fajlovi uspješno učitani');
    }
  };

  return (
    <Modal
      open={true}
      onClose={onClose}
      title="KRETANJE SREDSTVA"
      leftButtonOnClick={onClose}
      rightButtonOnClick={handleSubmit(onSubmit)}
      rightButtonText={'Sačuvaj'}
      buttonLoading={isSaving}
      leftButtonText="Otkaži"
      content={
        <MovementForm>
          {currentItem?.status !== StatusesForMovableInventory.ZADUZENO && !initialDispatchType && (
            <Controller
              name="transaction"
              control={control}
              rules={{required: 'Ovo polje je obavezno'}}
              render={({field: {name, value, onChange}}) => (
                <Dropdown
                  name={name}
                  value={value}
                  onChange={onChange}
                  options={transactionOptionsToShow}
                  label="TIP KRETANJA:"
                  isRequired
                  error={errors.transaction?.message}
                />
              )}
            />
          )}

          {transactionType === DispatchType.revers && (
            <Controller
              name="target_organization_unit_id"
              control={control}
              rules={{required: 'Ovo polje je obavezno'}}
              render={({field: {name, value, onChange}}) => (
                <Dropdown
                  name={name}
                  value={value}
                  onChange={onChange}
                  options={locationOptions.filter(item => item.id !== orgUnitId)}
                  label="LOKACIJA:"
                  isRequired
                  error={errors.target_organization_unit_id?.message}
                />
              )}
            />
          )}

          {transactionType === 'allocation' && (
            <Controller
              name="target_user_profile_id"
              control={control}
              rules={{required: 'Ovo polje je obavezno'}}
              render={({field: {name, value, onChange}}) => (
                <Dropdown
                  name={name}
                  value={value}
                  onChange={onChange}
                  options={userOptions}
                  label="PRIMALAC:"
                  isRequired
                  error={errors.target_user_profile_id?.message}
                />
              )}
            />
          )}
          {transactionType === 'allocation' && (
            <Controller
              name="office_id"
              control={control}
              rules={{required: 'Ovo polje je obavezno'}}
              render={({field: {name, value, onChange}}) => (
                <Dropdown
                  name={name}
                  value={value}
                  onChange={onChange}
                  options={officeOptions}
                  label="LOKACIJA:"
                  isRequired
                  error={errors.office_id?.message}
                />
              )}
            />
          )}
          {(transactionType || status === StatusesForMovableInventory.ZADUZENO) && (
            <Controller
              name="date"
              control={control}
              rules={{required: 'Ovo polje je obavezno'}}
              render={({field: {name, value, onChange}}) => (
                <>
                  <Datepicker
                    onChange={onChange}
                    label={`DATUM ${
                      status === StatusesForMovableInventory.ZADUZENO
                        ? 'RAZDUŽENJA'
                        : transactionType === 'return-revers'
                        ? 'POVRATA'
                        : transactionType === DispatchType.convert
                        ? 'KONVERTOVANJA'
                        : transactionType === DispatchType.revers
                        ? 'SLANJA'
                        : 'ZADUŽENJA'
                    }:`}
                    name={name}
                    minDate={minDate ? new Date(minDate) : undefined}
                    selected={value ? new Date(value) : ''}
                    isRequired
                    error={errors.date?.message}
                  />
                </>
              )}
            />
          )}
          {(status === StatusesForMovableInventory.ZADUZENO || transactionType === DispatchType.convert) && (
            <FileUploadWrapper>
              <FileUpload
                icon={<></>}
                variant="secondary"
                onUpload={handleUpload}
                note={<Typography variant="bodySmall" content="Fajlovi:" />}
                buttonText="Dodaj fajl"
                files={files}
              />
            </FileUploadWrapper>
          )}
        </MovementForm>
      }
    />
  );
};

